package com.xiaomi.xmsf.push.service;

import android.app.IntentService;
import android.content.Context;
import android.content.Intent;
import android.content.pm.ApplicationInfo;
import android.content.pm.PackageInfo;
import android.content.pm.PackageManager.NameNotFoundException;
import android.content.pm.Signature;
import android.text.TextUtils;
import com.xiaomi.push.service.PushServiceConstants;
import java.util.ArrayList;
import java.util.List;

public class MiuiPushActivateService extends IntentService {
    public static final String ACTION_ACCOUNT_CHANGE = "com.xiaomi.xmsf.push.ACCOUNT_CHANGE";
    public static final String ACTION_APP_REGISTERED = "com.xiaomi.xmsf.push.APP_REGISTERED";
    public static final String ACTION_SCAN = "com.xiaomi.xmsf.push.SCAN";
    private static final String INTENT_KEY_APP_ID = "app_id";
    private static final String INTENT_KEY_PKG = "source_package";
    static final Signature[] MIUI_PLATFORM_SIGNATURES;

    static {
        MIUI_PLATFORM_SIGNATURES = new Signature[]{new Signature(""), new Signature(""), new Signature("")};
    }

    public MiuiPushActivateService() {
        this("miui_push_activate_service");
    }

    public MiuiPushActivateService(String name) {
        super(name);
    }

    protected void onHandleIntent(Intent intent) {
        Intent activatedIntent;
        if (ACTION_SCAN.equals(intent.getAction())) {
            for (String pkg : getPackages()) {
                if (!isPackageRegistered(pkg)) {
                    activatedIntent = new Intent(ACTION_SCAN);
                    activatedIntent.setPackage(pkg);
                    startService(activatedIntent);
                }
            }
        } else if (ACTION_ACCOUNT_CHANGE.equals(intent.getAction())) {
            for (String pkg2 : getPackages()) {
                if (isPackageRegistered(pkg2)) {
                    activatedIntent = new Intent(ACTION_ACCOUNT_CHANGE);
                    activatedIntent.setPackage(pkg2);
                    startService(activatedIntent);
                }
            }
        } else if (ACTION_APP_REGISTERED.equals(intent.getAction())) {
            String packageName = intent.getStringExtra(INTENT_KEY_PKG);
            String appId = intent.getStringExtra(INTENT_KEY_APP_ID);
            if (!TextUtils.isEmpty(packageName) && !TextUtils.isEmpty(appId)) {
                addRegisteredPackage(packageName, appId);
            }
        }
    }

    private List<String> getPackages() {
        List<String> packages = new ArrayList();
        List<ApplicationInfo> appInfos = getPackageManager().getInstalledApplications(128);
        if (appInfos != null) {
            for (ApplicationInfo info : appInfos) {
                if (info.metaData != null && info.metaData.containsKey("miui_push_app") && verifySignatures(info.packageName)) {
                    packages.add(info.packageName);
                }
            }
        }
        return packages;
    }

    private boolean verifySignatures(String packageName) {
        try {
            PackageInfo packageInfo = getPackageManager().getPackageInfo(packageName, 64);
            if (packageInfo.signatures != null) {
                for (Signature sig : packageInfo.signatures) {
                    for (Signature approvedSig : MIUI_PLATFORM_SIGNATURES) {
                        if (approvedSig.equals(sig)) {
                            return true;
                        }
                    }
                }
            }
        } catch (NameNotFoundException e) {
        }
        return false;
    }

    private boolean isPackageRegistered(String packageName) {
        return getSharedPreferences(PushServiceConstants.PREF_KEY_REGISTERED_PKGS, 0).contains(packageName);
    }

    public void addRegisteredPackage(String packageName, String appId) {
        getSharedPreferences(PushServiceConstants.PREF_KEY_REGISTERED_PKGS, 0).edit().putString(packageName, appId).commit();
    }

    public static void awakePushActivateService(Context context, String action) {
        Intent intent = new Intent(context, MiuiPushActivateService.class);
        intent.setPackage(context.getPackageName());
        intent.setAction(action);
        context.startService(intent);
    }
}
